const float bluramount  = 20.0;
const float center      = 1.1;
const float stepSize    = 0.004;
const float steps       = 37.0;

const float minOffs     = (float(steps-1.0)) / -2.0;
const float maxOffs     = (float(steps-1.0)) / +2.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 tcoord = fragCoord.xy;

    float amount;
    vec4 blurred;

        //Work out how much to blur based on the mid point 
    amount = pow((tcoord.y * center) * 2.0 - 1.0, 2.0) * bluramount+iTime*1024.;

        //This is the accumulation of color from the surrounding pixels in the texture
    blurred = vec4(0.0, 0.0, 0.0, 1.0);

        //From minimum offset to maximum offset
    for (float offsX = minOffs; offsX <= maxOffs; ++offsX) {
        for (float offsY = minOffs; offsY <= maxOffs; ++offsY) {

                //copy the coord so we can mess with it
            vec2 temp_tcoord = tcoord.xy;

                //work out which uv we want to sample now
            temp_tcoord.x += offsX * amount * stepSize;
            temp_tcoord.y += offsY * amount * stepSize;

                //accumulate the sample 
            blurred += texture(iChannel0, temp_tcoord*2046.);

        } //for y
    } //for x 

        //because we are doing an average, we divide by the amount (x AND y, hence steps * steps)
    blurred /= float(steps * steps+iTime);

        //return the final blurred color
    fragColor = blurred+vec4(-0.025, 0.025, 0.6, 1.0);

} //main
