void mainImage( out vec4 f, vec2 g )
{
	vec2 p = g /= iResolution.y / 5.; f.x=9.;

    float t = iTime;

    for(int x=-1;x<=1;x++)
    for(int y=-1;y<=1;y++)
    {	
        p = vec2(x,y);
		p += .5 + .35*sin( t + 9. * fract(sin((floor(g)+p)*mat2(2,5,5,2)))) - fract(g);
        f = min(f, max(max(abs(p.x*.866 - p.y*.5), abs(-p.x*.866 - p.y*.5)), abs(p.y)));
    }

    f -= f - (mix(0.8-f.x, f.x, sin(t*.5)*.5+.5));
}
