//
int getstage()
{
    float range = sin(iTime);
    if(range < 0.2)
        return 1;
    else if(range < 0.4)
        return 2;
    else if(range < 0.6)
        return 3;
    else if(range < 0.8)
        return 4;
    else
        return 5;
}

float getpercent()
{
    float range = sin(iTime);
    int(range*100.0)/20;
    return range;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float percent = abs(sin(iTime));
    vec2 uv = fragCoord/iResolution.xy;

    vec2 nuv = uv*2.0-1.0;
    float dis = distance(nuv,vec2(0.0,0.0));
    vec2 dir = normalize(nuv)*dis*percent;
    nuv = percent * dir;
    uv = (nuv + 1.0)*0.5;


    vec4 col1 = texture(iChannel0,uv);
    vec4 col2 = texture(iChannel1,uv);


    vec4 finalcolor = col2;//vec4(mix(col1.rgb,col2.rgb,percent),1.0);
    if(percent>0.5)
    {
        finalcolor = col1;
    }

    fragColor = vec4(finalcolor.rgb,1.0);
}
