vec4 star( vec2 fragCoord, vec3 starPos )
{
    float d = length( (fragCoord.xy*2.0 - iResolution.xy) / iResolution.y - starPos.xy/starPos.z );
	return vec4( vec2(.005)/d/starPos.z, 1.0, 1.0 );   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor = vec4(0);
    for ( int i = 0; i <= 8; i++ )
    for ( int j = 0; j <= 8; j++ )
    {
        float z = 1.0+sin( iTime+float(i+j*2)/10.0 )*.3;
    	fragColor += star( fragCoord, vec3(float(i)/4.0-1.0,float(j)/4.0-1.0,z) );
    } 
}
