void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //pattern center point
    vec2 center = vec2(iResolution/2.0);

    //postion of inside and outside rings to set size and thickness 
    float outRadius = 91.0;
    float inRadius = 90.0;

    //multiplier for rotation offset
    float spinMulti = 80.0;

    //number of rings in chain
	const int ringCount = 20;

    //multiplier for pattern speed
    float timeMulti = 0.50;

    //offsets for distorting x and y postions of pattern
    int xOffset = 5;
    int yOffset = 10;

    vec2 rings[ringCount];

    rings[0] = center;

    for(int x = 0;x<ringCount;x++){

        rings[x] = rings[x-1] + vec2( 
            sin(iTime * float(x + xOffset) * timeMulti) * spinMulti,
            cos(iTime * float(x + yOffset) * timeMulti) * spinMulti
        );

        if(distance(fragCoord,rings[x-1]) < outRadius && distance(fragCoord,rings[x-1]) > inRadius){

            if(distance(rings[x],fragCoord) > 50.0 ){
                fragColor = vec4(0,0,1,1);
            }
            else{
            	fragColor = vec4(1,0.5,0,1);
            }

    	}
    }
}
