void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rad1 = 0.1;
    float rad2= 0.1;
    vec2 center1 = vec2(0.2,0.2);
    vec2 center2 = vec2(0.3,0.3);
    vec3 col1= vec3(1.0,0.5,0.3);
    vec3 col2= vec3(0.9,0.6,0.9);
    vec3 col3= vec3(0.2,0.0,0.7);
    vec3 bg = vec3(1.0,1.0,1.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    float offset = 0.1*sin(iTime);
    uv.y = uv.y * (iResolution.y) / iResolution.x;
    center1.x += offset;
    if (distance(uv, center1) < rad1 && distance(uv,center2) < rad2)
        fragColor = vec4(col3, 1.0);
    else if (distance(uv, center1) < rad1)
        fragColor = vec4(col1,1.0);
    else if (distance(uv, center2) < rad2)
        fragColor= vec4(col2,1.0);
    else
        fragColor = vec4(bg,1.0);

}
