void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color = vec3(0.0);
    float alpha = 1.0;

    if(uv.x > 0.0) 	{ color = vec3(0.0,0.5,0.2); 	}
    if(uv.x > 0.33) { color = vec3(1.0); 			}
    if(uv.x > 0.66) { color = vec3(0.9,0.2,0.2); 	}

	fragColor = vec4(color,alpha);
}
