void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = 0.07;
    float b = 25.0;
    float c = 3.0;
    float d = 2.0;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = uv.y + (a * sin(uv.x *b  + (sin(iTime*c)*d))) ;
    vec3 col1;
    vec4 tex = texture(iChannel0,uv);
    col1 = tex.rgb;
	fragColor = vec4(col1,1.0);
}
