// simple X and Y stretching of circular pattern created as a talking point in 
// the creative dev shader slack channel https://creative-dev.herokuapp.com/
// - Sterling Crispin Jan 23 2017
// do whatever you want with this mit license

float PI = 3.14159265359;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float twopi = 6.28318530718;
    float cosTime = cos(iTime);
    float sinTime = sin(iTime);

    float zoom = 1.3;
    float warpspeed = cosTime * (sinTime* zoom);

    float warpfactorX = sinTime * warpspeed;
    float warpfactorY = cosTime * warpspeed * cos(uv.y*sinTime*49.0);
    float scale = 0.5;
    float circle = sin( uv.x * scale * twopi) + cos( uv.y * scale  * twopi );
    // these are really arbitrary
    float edges = mod(circle, 0.3 )  ;
	float crudX = warpfactorX - edges;
    float crudY = abs(warpfactorY - edges);
    crudX *= 0.3;
    crudY *= 0.3;

    float R = circle ;
    float G = circle + crudX;
    float B = circle - crudY;

    R = circle;
    G = circle;
    B = circle;
    fragColor = vec4(
        clamp(R, 0.0,1.0),	
        clamp(G, 0.0,1.0),	
        clamp(B, 0.0,1.0),	
        1);


}
