#define hash(p) fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)


void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    O -= O;

    U = U+U - R;
    float d = length(U) - .6*R.y;
    d = min( d, length(U+R/3.) - .4*R.y ) ;
    d = min( d, length(U-R/3.) - .4*R.y ) ;

    if (U.x < 0.) O = vec4( smoothstep(2.,-2.,d ) );
    O = max(O, vec4(max(U.x,U.y)<-R.y/6.));

    float ants = fract( hash(floor(U/6.)) + 2.*iTime );
    //float ants = fract( U.x/30. + 3.*iTime );
    //float ants = fract( sin(U.x/10.)*sin(U.y/10.) + 3.*iTime );

    O = mix(O, vec4(ants), smoothstep(3.,0.,abs(d)) );
}
