{
    vec2 uv = Fcr.xy / Res.xy;
    fragColor = texture(iChannel0, uv);

}

#define Res iResolution
#define Fcr fragCoord
#define T iTime

#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

mat2 R2(float a)
{
float s = sin(a);
float c = cos(a);
return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mx = max(Res.x, Res.y);
    vec2 uv = Fcr.xy / Res.xy;
    vec2 uvn = (Fcr.xy - .5*Res.xy)/ mx;
    vec3 c = vec3(.0);
    vec2 g1 = 0.5-fract(R2(.1*T)*uvn*20.);
    vec2 g2 = 0.5-fract(R2(-.1*T)*uvn*20.);
    vec3 cbb = texture(iChannel0, uv).xyz;
    vec3 colorize = .6+.5*vec3(sin(2.29*(T+13.)), sin(.73*(T+31.)), sin(1.35*T));
    c = vec3(smoothstep(0.05, 0.01, length(g1*g2)))*colorize;
    fragColor = vec4((c+9.*cbb)/9.2, 0.0);
}
