const float mult   = 1.5;
const float flop   = .65; // for old version, use 0.

const bool  useTex = false;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 tex = texture(iChannel1, uv).rgb;

    uv.y *= mult; uv.y -= mult / 2.;


    float a1 = texture(iChannel0, vec2( uv.x, 0.25)).x;
	float a2 = texture(iChannel0, vec2( 1. - uv.x, 0.25)).x;

    float c  = abs(uv.y - flop) < a1 ? a1 : 0.;
          c += abs(uv.y + flop) < a2 && flop > 0. ? a2 : 0.;

    vec3  col = vec3(c, 0.15 * c, 0.35 * c);

    if (useTex)
    	fragColor = vec4(mix(tex, col, c * 2.), 1.0);
    else
        fragColor = vec4(col, 1.);
}
