void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy)*2.-1.;
    vec2 px = vec2(uv.x*iResolution.x/iResolution.y,uv.y);

    float x1 = cos(1.44*iTime);
    float x2 = cos(1.66*iTime);
    float x3 = cos(1.77*iTime);

    float d = distance(px,vec2(x1,0)) * distance(px,vec2(-x2,0)) / distance(px,vec2(0.5*x3,0));

    float freq = d * exp(3.*(.55+.45*cos(1.0*iTime)));

    float r = step(0.9,sin(freq));
    float g = step(0.9,sin(1.5*freq));
    float b = step(0.9,sin(2.2*freq));

    fragColor = vec4(r,g,b,1.0);
}
