const float PI = 3.141592653589793;

mat2 R(float a)
{
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(0);

    vec2 uv = fragCoord / iResolution.xy * PI;
    uv.x = uv.x * 2. * PI;
    uv.y = (uv.y - .5) * PI;

    vec3 p = vec3(1, 0, 0);
    p.xy *= R(uv.y);
    p.xz *= R(uv.x);

    p.g = 0.;

    //p = p.yxz; // blue/green
    //p = p.xzy; // red/green
    p = p.xyz; // red/blue seems to get biggest effect

    col = p;
    fragColor = vec4(col, 1);
}
