//Variables for rows and columns
float rows = 7.0;
float columns  = 13.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Creates black and white colors
    vec4 black = vec4(0.0,0.0,0.0,1.0);
    vec4 white = vec4(1.0,1.0,1.0,1.0);
    vec4 newColor;

    //Creates variables for rows and columns
    float col = (mod(floor(uv.x * columns), 2.0));
    float row = (mod(floor(uv.y * rows), 2.0));

    //Checks if row and col is equal to each other, if so, set color to black 
	if (row == col)
    {
        newColor = black; 
    }  

    //If not then set color to white
    else
    {
       newColor = white; 

    }

    // Output to screen
    fragColor = newColor;
}
