vec2 times(vec2 a, vec2 b)
{
    float re = a.x*b.x-a.y*b.y;
    float im = a.x*b.y+a.y*b.x;
    return vec2(re, im);
}

vec2 power(vec2 a, int n)
{
    vec2 res = a;
    for(int i=0; i<n-1; i++) res=times(res,a);
    return res;    
}

mat2 rm(float t)
{
    return mat2(cos(t),-sin(t),sin(t),cos(t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y*1.5;
    uv *=rm(iTime);

    vec2 z = vec2(1,0)-power(uv,5);
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float a = mod(log(length(z)),0.5);
    col *= a+ 0.25*z.x/length(z)+0.75;

    // Output to screen
    fragColor = vec4(col,1.0);
}
