void mainImage( out vec4 col, in vec2 p ) {
	float t = iTime;
    p /= iResolution.xy;

    p.x = 1.-p.x;
    p.y=1.-p.y;
    float r = 0.0;
    float pass = 0.0;

    for (int i = 0; i < 10; i++) {
        r += smoothstep(0.3, 0.4, texture(iChannel0, vec2(p.x - pass*0.01, p.y)).r);
        pass += 1.0;
    }
    r /= pass;

    float g = 0.0;
    pass = 0.0;

    for (int i = 0; i < 10; i++) {
        g += smoothstep(0.3, 0.4, texture(iChannel0, vec2(p.x - pass*0.01, p.y + pass*0.01)).g);
        pass += 1.0;
    }
    g /= pass;

    float b = 0.0;
    pass = 0.0;

    for (int i = 0; i < 10; i++) {
        b += smoothstep(0.3, 0.4, texture(iChannel0, vec2(p.x, p.y + pass*0.01)).b);
        pass += 1.0;
    }
    b /= pass; 

    col.rgb = vec3(0.0) + vec3(r, g, b);
}
