void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float ox = 0.0;
    float oy = 1.0;
    vec2 ofst = vec2(ox,oy);
    float l = 0.25;
    float r = 0.25;
    float u = 0.25;
    float d = 0.25;
    float sx = 2.0;
    float sy = 2.0;
    vec2 scl = vec2(1.0,1.0)/vec2(sx,sy);

    uv *= scl;
    uv *= vec2(1.0+l+r, 1.0+u+d);
    uv += ofst * scl;
    uv += vec2(-l, -u) * scl;

    // Time varying pixel color
    vec4 tex = texture(iChannel0, uv);

    uv = fract(uv*2.0)/2.0;
    vec2 col;
    col.x = mix(0.0 ,1.0, uv.x);
    col.y = mix(0.0, 1.0, uv.y);

    // Output to screen
    fragColor = vec4(col,0.0,1.0) + tex;
}
