// SpongeBob is careful to avoid bugs

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Draw a yellow background
    fragColor = vec4(1.0, 1.0, 0.0, 1.0);

    // Draw SpongeBob in the center of the screen
    vec2 center = vec2(0.5, 0.5);
    vec2 delta = abs(uv - center);
    if (delta.x < 0.15 && delta.y < 0.2)
    {
        fragColor = vec4(1.0, 0.8, 0.2, 1.0); // skin color
        fragColor = mix(fragColor, vec4(0.0, 0.0, 0.0, 1.0), 0.3); // add some shading
    }

    // Draw a bug at a random position
    vec2 bugPos = vec2(0.8, 0.8);
    delta = abs(uv - bugPos);
    if (delta.x < 0.05 && delta.y < 0.05)
    {
        fragColor = vec4(1.0, 0.5, 0.5, 1.0); // bug color
    }

    // Draw a speech bubble coming from SpongeBob's mouth
    vec2 bubblePos = vec2(0.5, 0.65);
    delta = abs(uv - bubblePos);
    if (delta.x < 0.1 && delta.y < 0.05)
    {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0); // bubble color
    }

    // Draw some text inside the speech bubble
    vec2 textPos = vec2(0.5, 0.625);
    if (length(uv - textPos) < 0.03)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0); // text color
    }
}
