void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float time = iTime;

    // Calculate the brightness of the pixel based on its coordinates and the time
    float brightness = sin(fragCoord.x * 0.1 + time) * cos(fragCoord.y * 0.1 + time);

    // Use the brightness to create a pulsating color
    vec3 col = vec3(0.5 + 0.5 * brightness, 0.5 + 0.5 * brightness, 1.0);

    // Use the time uniform to create a swirling effect
    float swirl = sin(sqrt(pow(fragCoord.x - 0.5, 2.0) + pow(fragCoord.y - 0.5, 2.0)) * 0.2 + time);
    col *= vec3(swirl, swirl, swirl);

    // Use the color from the vertex shader to create a gradient
    fragColor = vec4(col, 1.0);
}
