void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy;

    // If no mouse input, animate mouse
    if (mouse == vec2(0)) {
        mouse = vec2(sin(iTime) / 2. + .5) * iResolution.xy;
    }

    vec2 uv = fragCoord/iResolution.xy;
    float size = 50.;
    vec2 p = ceil(fragCoord / size) * size; 
    float progress = (p.x + p.y) / (mouse.x + mouse.y);

    vec2 v = abs(fragCoord - p + vec2(size/2.));
    fragColor = vec4(vec3(0.), 1.);
    if (max(v.x, v.y) * progress < size/2.) {
        fragColor = vec4(vec3(1.), 1.);
    }
}
