#define R iResolution.xy
#define S smoothstep
#define P 3.141592

float eye (vec2 uv, vec2 M) {
    float d = .5 + .5 * sin(length(uv - M) * 150.);
    float c = S(1.5/R.y, 0., d - .5);
    c -= S(0., .5/R.y, abs(length(uv - M) - .53) - .3);
    c = clamp(c, 0., 1.);
    c += S(1.5/R.y, 0., length((uv - M * 1.2) + vec2(.05,-.05)) - .03);
    c += S(1.5/R.y, 0., length((uv - M * 1.1) + vec2(.1,-.1)) - .01);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*R.xy)/R.y;
    vec2 M = .2*(iMouse.xy-.5*R.xy)/R.y;

    float e = eye(uv, M);
    float t = -0.1 + sin(iTime) * .1;
    float c = abs(uv.y) + t + cos(uv.x * P + P) * .1;
    float m = S(1.5/R.y, 0., c);

    c = .5 + .5 * sin(c * 150.);
    c = S(3./R.y, 0., c - .5);

    vec3 col = vec3(mix(c, e, m));
    fragColor = vec4(col,1.0);
}
