// Buffer A - main rendering
// Buffer B - bokeh dof 

// tonemap
vec3 ACES(vec3 x) {
    float a = 2.51;
    float b =  .03;
    float c = 2.43;
    float d =  .59;
    float e =  .14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

// bloom function
vec4 bloom(sampler2D sam, vec2 p) {
    vec4 col = vec4(0);
    const int N = 8;

    for (int i=-N; i<=N; i++)
    for (int j=-N; j<=N; j++) {
        vec2 off = vec2(i,j) / float(N);
        col += textureLod(sam, p+off*.04, 3.);
    }
    return col/col.a;
}

// radial blur function
// v is the direction vector
vec4 radialBlur(sampler2D sam, vec2 p, vec2 v) {
    vec4 col = vec4(0);
    const float N = 64.; // samples

    for (float i=0.; i<1.; i+= 1./N) {
        col += pow(1.-sqrt(i),3.)*texture(iChannel0, p+.3*i*v);
    }

    return col/col.a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = fragCoord/iResolution.xy;

    // base color
    vec3 col = texture(iChannel0, p).rgb;
    // bloom
    vec3 bloom = pow(bloom(iChannel0, p).rgb, vec3(2));
    // radial blur
    vec2 v = normalize(vec2(1)-p); // direction
    vec3 blur = .2*radialBlur(iChannel0, p, v).rgb;

    col += blur;
    col = pow(col, vec3(.4545)); // gamma correction
    col += bloom;

    col = ACES(col);
    col = mix(col, dot(col, vec3(1))/vec3(3), -.6); // boost the saturation
    col = pow(col, vec3(.84,1,.9)); // color grade

    // vignette
    col *= pow(64.*p.x*p.y*(1.-p.x)*(1.-p.y),.1);

    // output
    fragColor = vec4(col,1.0);
}

// time
#define time iTime

// rotation function
mat2 rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

// hash vec2 to float
float hash21(vec2 p) {
    p = fract(p*vec2(452.127,932.618));
    p += dot(p, p+123.23);
    return fract(p.x*p.y);
}

// noise function by me
float noise(vec2 p) {
    vec2 q = floor(p);
    vec2 f = fract(p);
    f = f*f*(3.-2.*f);
    return mix(mix(hash21(q+vec2(0,0)),hash21(q+vec2(1,0)),f.x),
               mix(hash21(q+vec2(0,1)),hash21(q+vec2(1,1)),f.x),f.y);
}

// fractal noise
float fbm(vec2 p) {
    float f = 0.;
    f += .5*noise(p);
    f += .25*noise(p*2.);
    f += .125*noise(p*4.);
    f += .0625*noise(p*8.);
    return f;
}

// leave sdf
float sdLeave(vec2 p) {    
    float d = length(p*vec2(1,.9)-vec2(0,.02))-.2+.9*abs(p.x);

    p.x = abs(p.x);

    vec2 q = p;
    q -= vec2(0,-.2+32.*pow(p.x,5.));
    q *= rot(3.141592*.3+.1*fbm(16.*p));
    q -= vec2(0,.17);
    d = min(d, length(q)-.17+2.*abs(q.x));

    q = p;
    q -= vec2(-.07,-.1+6.*pow(p.x,3.));
    q *= rot(3.141592*.7+.1*fbm(24.*p));
    q -= vec2(0,.13);
    d = min(d, length(q)-.13+2.5*abs(q.x));

    // stem
    q = p;
    float h = clamp(q.y,-.377,0.);
    q -= vec2(0,h);
    d = min(d+.2*pow(fbm(24.*p),3.), length(q)+.02*h);

    return d;
}

// sphere intersection function
// thanks to iq: https://iquilezles.org/articles/intersectors/
vec2 sphIntersect(vec3 ro, vec3 rd, vec3 ce, float ra) {
    vec3 oc = ro - ce;
    float b = dot(oc, rd);
    float c = dot(oc, oc) - ra*ra;
    float h = b*b - c;
    if(h<0.) return vec2(-1);
    h = sqrt(h);
    return vec2(-b-h, -b+h);
}

// 3d cartesian to 2d polar coordinates
vec2 polar(vec3 p) {
    return vec2(atan(p.x,p.z),p.y);
}

// leave in 3d
float leaveSphere(vec3 col, vec3 ro, vec3 rd, vec3 ce, vec2 an, float ra) {
    vec2 t = sphIntersect(ro, rd, ce, ra);

    if (t.y<0.) return 0.; // no intersection

    vec3 oc = ro - ce;
    vec3 p = oc + rd*t.x; // front point
    vec3 q = oc + rd*t.y; // back point

    // rotation, very ugly code
    p.xz *= rot(an.x);
    q.xz *= rot(an.x);
    p.yz *= rot(an.y);
    q.yz *= rot(an.y);


    float di = ra*2.; // diameter
    vec2 r = vec2(ra,1)/di; // ratio
    vec2 pf = polar(p)*r; // polar front point
    vec2 pb = polar(q)*r; // polar back point

    // leave back and front
    float lf = step(sdLeave(pf), 0.);
    float lb = step(sdLeave(pb), 0.);

    // return alpha values
    return lf*(1.-lb)+lb;
}

// hash functions by me
float hash1(inout float n) {return fract(sin(n+=1.)*2348.3241);}

vec2 hash2(inout float n) {return fract(sin(n+=1.)*vec2(2348.3241,4591.5392));}

vec3 hash3(inout float n) {return fract(sin(n+=1.)*vec3(2348.3241,4591.5392,3412.4231));}

// rendering function
vec3 render(vec3 ro, vec3 rd) {
    vec3 col = vec3(0);

    vec2 an = time*vec2(.2,.3); // angle

    for (float i=0.; i<1.; i+=1./80.) { // generate 80 leaves
        float n = i+1.; // randoms seed
        // xy point
        vec2 p = (.5-fract(hash2(n)+vec2(-.06,.05)*time))*vec2(15,13);

        // change the angle
        an += hash1(n)-.5;

        // color
        vec3 mat = hash3(n);
        mat = vec3(.3+.7*mat.r,.3+.7*mat.g*mat.g,mat.b);

        vec3 ce = vec3(p,8.*(1.-i)); // center
        float ra = .7+.6*hash1(n); // radius

        // alpha value
        float alpha = leaveSphere(col, ro, rd, ce, 3.141592*an, ra);

        col = mix(col, mat, alpha);
    }

    // light direction
    vec3 lig = vec3(.57735026919);

    // light glare + noise to simulate volumetric scattering in fog
    float glare = pow(clamp(dot(rd, lig),0.,1.),16.);
    col += .8*fbm(12.*rd.xy+.1*time*vec2(1,3))*(.004+glare);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates centered at the origin
    vec2 p = (fragCoord - .5*iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0,0,-3); // ray origin
    vec3 rd = normalize(vec3(p,1.5)); // ray direction

    vec3 col = render(ro, rd);

    // output
    fragColor = vec4(col*1.2,1.0);
}

// bokeh dof buffer

// bokeh function
vec4 bokeh(sampler2D sam, vec2 p, float b) {    
    vec4 col = vec4(0);
    const int N = 6; // samples

    for (int i=-N; i<=N; i++)
    for (int j=-N; j<=N; j++) {
        vec2 off = vec2(i,j) / float(N); // offset

        if (dot(off, off)<1.) { // inside disk
            // blurred image
            col += texture(iChannel0, p + b*off*vec2(9./16.,1));
        }
    }
    return col/col.a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = fragCoord/iResolution.xy;

    float b = dot(p-.6,p-.5)*.05; // blur value
    vec3 col = bokeh(iChannel0, p, b).rgb;

    // output
    fragColor = vec4(col,1.0);
}
