#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage( out vec4 O, vec2 u )
{
	float t = iTime*.01+.25, s=.1, f=1. ,a,l;
	vec3  R = iResolution,p,
	      D = vec3( ( u+u - R.xy ) / R.x*.2 , .5 ),
          M = 2.*iMouse.xyz/R,
          o = vec3(1,.5,.5) + vec3(t+t, t, -2);
    O *= 0.;
	mat2 r1 = rot(.5+M.x),
	     r2 = rot(.8+M.y);
	D.xz *= r1; o.xz *= r1;
	D.xy *= r2; o.xy *= r2;

	for (int i,r; r++<20; f*=.73, s+=.1 ) {
		p = abs( mod( o + s*D ,1.7) -.85 );
		a=t=0.;
		for ( i=0; i++<17; t=l )
			l = length( p= abs(p)/dot(p,p) - .53 ),
			a += abs(l-t);

		a *= a*a;
		r>7 ? f *= min(1., .7 + a*a/1e3 ) : f; 
		O.rgb += f + s*vec3(1,s,s*s*s)*a*.0015*f; 
	}

	O = .0085*O + .0015*length(O);	
}
