#define PI 3.14159265358979

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);

    vec2 uv = (fragCoord.xy - 0.502*iResolution.xy) / iResolution.y; 

    float a = atan(uv.x, uv.y);
    float r = length(uv) - 0.3;
    vec2 st = vec2(a/PI*2.0, 0.1/r) + log(sin(-5.0*r - 10.0)) - 0.2*iTime;

    vec4 col = texture(iChannel0, st);
    col *= 0.01*r - log(r);

    fragColor = vec4(col);
}
