void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float magnitude = length(uv);

    magnitude -= 0.5;
    //magnitude -= sin(magnitude);
    //magnitude = sin(magnitude * 8.0) / 8.0;
    //magnitude = sin(magnitude * 8.0 + iTime) / 8.0;

    magnitude = abs(magnitude);

    //magnitude = step(0.1, magnitude);
    magnitude = smoothstep(0.01, 0.15, magnitude);

    fragColor = vec4(magnitude,0,0.5,1.0);
}
