void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x / iResolution.y;

    float spd = 1.0;

    uv.x = fract(uv.x - spd * iTime);
    uv.y = fract(uv.y);
    uv -= 0.5;
    uv *= 2.0;

    float dist = length(uv);
    dist = sin(dist + iTime) * uv.x * uv.y;
    dist = abs(dist);
    dist = step(0.1, dist);

    vec3 col = vec3(0.0);

    if (uv.x > 0.0 && uv.y > 0.0) {
        // yellow
        col = vec3(uv.x * uv.y,uv.x * uv.y,0.0);
    } else if (uv.x < 0.0 && uv.y > 0.0) {
        // green
        col = vec3(0.0, uv.y * -uv.x,0.0);
    } else if (uv.x > 0.0 && uv.y < 0.0) {
        // red
        col = vec3(uv.x * -uv.y, 0.0, 0.0);
    } else {
        //blue
        col = vec3(0.0, 0.0, uv.x * uv.y);
    }


    col *= dist;


    // Output to screen
    fragColor = vec4(col, 1.0);
}
