void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float freq = 1.0;

    float wave = (cos(iTime * freq) + 1.0) * .5;

    float size = (1.0 - wave) * 50.0 + 50.0;
    //size = 50.0;


    vec4 disp = texture(iChannel1, uv);

    float noiseX = disp.r * wave * 0.4;
    float noiseY = disp.g * wave * 0.2;

    float wave2 = (cos(iTime * freq * .5) + 1.0) * .5;

    float noiseDirectionX = wave2 > 0.5 ? 1.0 : -1.0;
    float noiseDirectionY = wave2 > 0.5 ? -1.0 : 1.0;

    vec2 noise = vec2(noiseX * noiseDirectionX, noiseY * noiseDirectionY);

    vec4 img = texture(iChannel0, uv + noise);

    vec2 origin = ceil(uv * size) / size; 
    origin -= (1. / size) * 0.5;


    float dist = distance(uv, origin);
    dist = smoothstep(0.0, 0.03, dist);

    vec3 col = vec3(1.- dist);


    // Output to screen
    fragColor = img;
    fragColor *= vec4(col, 1.0);

}
