float point_aabb_sdf_non_exact(vec2 uv, vec2 lower, vec2 upper)
{
    float box_dist = max(max(-uv.x + lower.x, uv.x - upper.x),
                         max(-uv.y + lower.y, uv.y - upper.y));
    // Note: take corners into account to get L2 distance
    return box_dist;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float c = point_aabb_sdf_non_exact(uv, vec2(.25, .25), vec2(.75, .75));
    fragColor = vec4(vec3(c), 1.0);
}
