const float Pi = 3.14159265358979;
const vec2 Size = vec2(8, 8);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy / Size; // Block resolution
    vec2 p = mod(fragCoord, r); // Block relative coordinate
    vec2 f = floor(fragCoord / r); // Block spatial frequency
    vec2 c = cos((2. * p + 1.) * f * Pi / (2. * r));
    float v = c.x * c.y;
    fragColor = vec4(.5 + .5 * v);
    if (p.x < 1. || p.y < 1.) // Red block edge line
        fragColor = vec4(1, 0, 0, 0);
}
