// 中心点
const vec2 pivot = vec2(0.5, 0.5);
// 采样间隔(deg)
const float delta = radians(3.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.0);
    float angle;
    vec2 dir = uv - pivot;
    vec2 xy;

    float time = mod(float(iFrame), 130.0);
    float samples = step(30.0, time) * (time - 30.0) + 1.0;
    for (float i=0.0; i<samples; i+= 1.0)
    {
        angle = delta * i;
        xy.x = dir.x * cos(angle) - dir.y * sin(angle);
        xy.y = dir.x * sin(angle) + dir.y * cos(angle);
        xy += pivot;

        col += texture(iChannel0, clamp(xy, 0.0, 1.0)).rgb;
    }
    col /= samples;

    // Output to screen
    fragColor = vec4(col,1.0);
}
