void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float noise = sin(10.0*uv.x + iTime) * cos(10.0*uv.y + iTime);
    float angle = 3.14 * noise;

    float lineAngle = atan(uv.y, uv.x);  

    float diff = abs(lineAngle - angle); 
    diff = min(diff, 3.14 * 2.0 - diff); 

    float line = smoothstep(0.02, 0.03, diff);

    vec3 color = vec3(1.0 - line); 

    fragColor = vec4(color,1.0);
}
