float heartShape( vec2 uv){
    uv *= 0.4;
    uv.x = abs(uv.x);
    uv.y = -0.15 -uv.y*1.2 + uv.x*(1.0-uv.x);
    float c= length(uv)-0.5;
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 P = 4. * (fragCoord.xy - iResolution.xy /2.) / min(iResolution.x, iResolution.y); //uv
    vec2 uv = P;
	float d = heartShape(uv);
    vec3 col = 1.0+ cos(iTime+vec3(0,2,4));
	fragColor = vec4(mix(col, cos(iTime*2.+vec3(.0))-.2, clamp(d / 0.01, 0.4, 1.0)),1.0);
}
