const float radius = 2.0;
const float PI = 3.141592653589793238;

const vec3 color0 = vec3(0.0, 0.0, 0.0);
const vec3 color1 = vec3(0.0, 1.0, 0.945);
const vec3 color2 = vec3(0.945, 0.0, 1.0);

float smootherstep(float a, float b, float x) {
    x = clamp((x - a)/(b - a), 0.0, 1.0);
    return x * x * x * (x * (x * 6.0 - 15.0) + 10.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec2 rotate1 = vec2(cos(iTime) * 1.5, sin(2.0 * iTime)) * 0.5;
    vec2 rotate2 = vec2(cos(iTime + PI) * 1.5, sin(2.0 * iTime + PI)) * 0.5;

    float gradient1 = smootherstep(radius, -radius, length(uv - rotate1));
    float gradient2 = smootherstep(radius, -radius, length(uv - rotate2));

    vec3 color = gradient1 * color1 + gradient2 * color2;
    vec3 background = (1.0 - (gradient1 + gradient2)) * color0;
    vec3 finalColor = color + background;

    fragColor = vec4(finalColor, 1.0);
}
