void mainImage( out vec4 O, vec2 U )
{
    O = T(U-vec2(1,0));
    if ( U.x>1.) return;
    if (U.y<1.) { O.a = iTime; return; }
    U /= R;
#define plot(v)     step( abs( U.y - .8* (v) /60. ) , 1./R.y )
    O.r = plot( iFrameRate );
    O.g = plot(1./iTimeDelta  );
    O.b = plot(1./ (iTime-T(0).a) ); // same that iTimeDelta !

    O.a = 0.;
    for (float i = 0.; i < 40000.*(.5+.5*sin(iTime)) ; i++ )
        O.a--;
}
