float i(in vec2 a) { return dot(a, a); }
float j(in vec2 c, in vec2 a, in vec2 d) {
  vec2 e = c - a, b = d - a;
  return i(e - b * clamp(dot(e, b) / dot(b, b), 0., 1.));
}
mat2 f(float a) {
  float b = sin(a), c = cos(a);
  return mat2(c, b, -b, c);
}
float g(vec2 d) {
  float c = .1;
  for (float a = 0.; a < 35.; a++) {
    float b = a + 1.;
    if (a > 34.)
      break;
    float e = cos(a / 35. * 5.5 * 6.28) * .1 * (a / 5.),
          k = sin(a / 35. * 5.5 * 6.28) * .1 * (a / 5.),
          l = cos(b / 35. * 5.5 * 6.28) * .1 * (b / 5.),
          m = sin(b / 35. * 5.5 * 6.28) * .1 * (b / 5.);
    c = min(c, j(d, vec2(e, k), vec2(l, m)));
  }
  return c;
}
vec2 h(inout vec2 b, vec2 a) {
  vec2 c = a * .5, d = floor((b + c) / a);
  b = mod(b + c, a) - c;
  return d;
}
float n(vec2 a) {
  float b = .1;
  vec2 c = h(a, vec2(.47, .46));
  mod(c.x, 2.) == 0.
      ? (a.xy *= f(0.), b = min(b, g(a * 3.)))
      : (a.xy *= f(3.141593), a.x += .045, a.y += .02, b = min(b, g(a * 3.5)));
  return b;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 a = fragCoord.xy / iResolution.xy;
  a = (a - .5) * 2., a.x *= iResolution.x / iResolution.y;
  vec3 c = vec3(0);
  float b = .1;
  a *= f(.785398), b = n(a), b = smoothstep(0., b, 1e-4), c += b,
                   fragColor = vec4(c, 1);
}
