void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float pi = 3.14159265358979323846264;
    float d=5.0*iMouse.x/iResolution.x,phase=-pi*0.5,maxit=50000.0;
    if (d==0.0) d=1.0;
	vec2 p=fragCoord.xy/iResolution.xy;
    float x=p.x*pi*2.0,xp=p.y*6.0-3.0;
	float i,sp=0.5*pi+phase,ssp=sin(sp);
	for (i=0.0;i<=maxit && x>=0.0 && x<=pi*2.0;i++)
	{
		xp+=d*(sin(x)-ssp); x+=d*xp;
		//x=mod(x,pi*2.0); // Increase accuracy for out-of-bucket laggards
	}
	//fragColor=vec4(x/(pi*2.0),0.5+xp*0.5,0.5,1.0);
	if (i>maxit) fragColor=vec4(0,0,0,1.0);
	else fragColor=vec4(0.01*i,min(1.0,0.1*i)-0.0001*i,0.5*i,1.0);
}
