void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 5.*fragCoord/iResolution.xx;

    vec4 p=vec4((fragCoord/4e2),0,-1280);
    for(int i=0;i<10;++i){
        p+=vec4(sin(-(p.x+iTime*.2))+atan(p.y*p.w),cos(-p.x)+atan(p.z*p.w),cos(-(p.x+sin(iTime*.8)))+atan(p.z*p.w),0);
    }

    fragColor = p;
}
