vec3 Rectangle( in vec2 uv, in float x, in float y, in float width, in float height ) {

    // Results in 1.0 value for pixels inside borders
    vec2 borders = step( vec2(x, y), uv ) * 1.0 - step( vec2(x + width, y + height), uv);

    // Return vec3 with 1.0 inside borders
    return vec3( borders.x * borders.y );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Colors
    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 blue = vec3(0.0, 0.0, 1.0);
    vec3 yellow = vec3(1.0, 1.0, 0.0);

    // Rectangles
    vec3 rect0 = Rectangle(uv, 0.0, 0.0, 0.2, 0.25);
    vec3 rect1 = Rectangle(uv, 0.8, 0.7, 0.2, 0.5);
    vec3 rect2 = Rectangle(uv, 0.0, 0.9, 0.2, 0.3);

    vec3 rect3 = Rectangle(uv, 0.09, 0.0, 0.01, 1.0);
    vec3 rect4 = Rectangle(uv, 0.19, 0.0, 0.01, 1.0);
    vec3 rect5 = Rectangle(uv, 0.79, 0.25, 0.01, 0.75);

    vec3 rect6 = Rectangle(uv, 0.0, 0.1, 0.2, 0.02);
    vec3 rect7 = Rectangle(uv, 0.0, 0.25, 1.0, 0.02);
    vec3 rect8 = Rectangle(uv, 0.0, 0.7, 1.0, 0.02);
    vec3 rect9 = Rectangle(uv, 0.0, 0.9, 1.0, 0.02);

    // Grey background with cutouts for rectangles
    vec3 color = vec3(0.85) - (rect0 + rect1 + rect2 + rect3 + rect4 + rect5 + rect6 + rect7 + rect8 + rect9);

    // Add rectangles with colors
    color += rect0 * red + rect1 * blue + rect2 * yellow;

    // Output to screen
    fragColor = vec4(color, 1.0);
}
