void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float d = length(uv);

    float circle1 = smoothstep(0.3, .29, d);
    float circle2 = smoothstep(0.2, .21, d);

    vec3 col = vec3(0.);
    col += circle1;
    col *= circle2;

    fragColor = vec4(col,1.0);
}
