void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y + vec2(.5);

    float spread = sin(radians(30.) * iTime);

    vec3 a = radians(360.) * (uv.x + spread * vec3(-1., 0., 1.) / 3.);
    a = clamp(a, vec3(0.), vec3(radians(360.)));
    vec3 col = vec3(.5 - .5 * cos(a));

    if (uv.y > .5) {
		fragColor = vec4(col, 1.);
    } else {
	    fragColor = vec4(smoothstep(6. / iResolution.y, -0., abs(2.5 * uv.y - col - .1)), 1.);
    }
}
