float noise(vec2 st) {
    return fract(sin(dot(st.xy, vec2(1.9898,23.233))) * 6.314159);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy; // Normalized coordinates

    vec4 imgColor = texture(iChannel0, uv); 
    float luminance = dot(imgColor.rgb, vec3(0.299, 0.587, 0.114));

    vec2 centeredUv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);

    float r = length(centeredUv);
    float theta = atan(centeredUv.y, centeredUv.x);

    float spiral = 0.8 * cos(2.0 * (theta + 45.0 * r - 0.9 * iTime)); // play numbers for funny results =))))

    float distort = noise(centeredUv) + 2.0; //at first it was a *, I couldn't decide, but I think it's more "creepy" when I use + =))))
    spiral += distort * luminance;

    vec3 col;
    col.r = smoothstep(1.0, 0.5, spiral);
    col.g = smoothstep(1.0, 0.4, spiral);
    col.b = smoothstep(1.0, 0.6, spiral);

    col *= 2.0 + 1.0 * cos(iTime + uv.xyx);

    fragColor = vec4(col,1.0);
}
