#define tiles653 8.

//rndom noise from https://www.shadertoy.com/view/sslfRn
vec2 Hash22(vec2 p) {
    p = vec2( dot(p,vec2(127.1,311.7)),
              dot(p,vec2(269.5,183.3)));

    vec2 result = normalize(-1.0 + 2.0 * fract(sin(p)*43758.5453123));
    result *= sin(22. * 1.0 + p.x * 3.14 + p.y * 3.14);
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2     uv = (fragCoord-.5*iResolution.xy)/iResolution.y-0.5;
    vec3    col = vec3(0.);
    vec2   grid = fract(uv * tiles653)-.5; 
    vec2     id = floor(uv * tiles653);
    vec2      n = Hash22(id); 
    float    n1 = n.x;
    float    n2 = n.y;
    float    n3 = n.x / n.y;
    float gridx = grid.x;
    float gridy = grid.y;

    if (n1 > 0.) gridx *= -1.;
    vec4 mask1 = texture(iChannel0,vec2(gridx,gridy)+0.5); 

    if (n1 < 0.5) gridy *= -1.;
    vec4 mask3 = texture(iChannel0,vec2(gridx,gridy)+0.5); 

    if (n2 > .75) {col = vec3(mask1);}
    else {col=vec3 (mask3);}

    vec3 mask4 = col;

    if (n2 < 0.25) gridx *= -1.;
    vec4 mask2 = texture(iChannel0,vec2(gridy,gridx)+0.5); 

    if (n3 > .5) {col= vec3(mask2);}
    else {col=vec3(mask4);}

    fragColor = vec4(col,1.0);
}
