#ifdef GL_ES
precision mediump float;
#endif

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float sc(in vec2 st, in float min, in float max) {
    return min+random(st)*(max-min);
}

float myrandom (vec2 st, vec2 ist) {
  float rAmplitude1 = sc(ist*2., 0.2, .4);
  float rFreq1 = sc(ist*3., .0005, .001);
  float val1 = sc(ist*4., 0.05, 2.) * rAmplitude1*(sin(random(ist*0.9284)+rFreq1*3.14*iTime)+1.);
  float rAmplitude2 = sc(ist*5., 0.2, .4);
  float rFreq2 = sc(ist*6., .0005, .001);
  float val2 = sc(ist*7., 0.05, 2.) * rAmplitude2*(sin(random(ist*0.9284)+rFreq2*3.14*iTime)+1.);


    return fract(sin(dot(st.xy,
                         vec2(val1,val2)))*
        43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.y;
    float reps = 5.;
    st*=reps;
    vec2 ipos = ceil(st);
	vec2 fpos = fract(st);
    float rnd = myrandom( st, ipos );

    fragColor = vec4(vec3(rnd),1.0);
}
