// Martijn Steinrucken
//
// https://twitter.com/the_artofcode?lang=en
// https://www.youtube.com/TheArtOfCodeIsCool 
//
// https://www.youtube.com/watch?v=PGtv-dBi2wE
// https://www.youtube.com/watch?v=Ff0jJyyiVyw
// https://www.youtube.com/watch?v=AfKGMUDWfuE
// https://www.youtube.com/watch?v=Vmb7VGBVZJA
//
// https://michaelwalczyk.com/blog-ray-marching.html
// https://www.lcg.ufrj.br/cwdc/13-webgl/shaderToy.mp4
//
// A very simple ray marching implementation,
// with a few SDF functions: sphere, torus, box, cylinder.
// Boolean operations are also available: 
// union, intersection, difference and blend.
// 
// The beauty of the method is that there are no more polygons or vertices, 
// but objects are just scalar fields, given by Signed Distance Functions (SDFs).
//
// Paulo Roma
// 17/07/2022

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01
#define PI 3.1415925359

// Color Functions

float g2l(float x) {
    return x <= 0.04045 ? x/12.92 : pow((x + 0.055)/1.055, 2.4);
}

vec3 g2l3(vec3 v) {
    return vec3(g2l(v.x), g2l(v.y), g2l(v.z));
}

// Rotation matrix.
mat2 Rot (float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

mat3 rotateY(float k) {
    float c = cos(k);
    float s = sin(k);
    return mat3(c,  0, -s,
                0,  1,  0,
                s,  0,  c);
}


vec3 rgb(vec3 color) {
  return color/255.;
}

// Shape, sf is the surface and rgb is the color
struct Shape {
    float sf; 
    vec3 color;
    bool isTexturized;
    bool isBumped;
};

Shape minShape(Shape spA, Shape spB) {
  if (spB.sf < spA.sf) return spB; 
  return spA;
}


// SDF of a torus at the origin given its two radius.
Shape dTorus(vec3 p, vec2 r, vec3 color) {
    Shape sp;
    sp.color = color;

    float x = length(p.xz) - r.x;
    sp.sf = length(vec2(x, p.y)) - r.y;
    return sp;
}

Shape dPrism( vec3 p, vec2 h, vec3 color ) {
  Shape sp;
  vec3 q = abs(p);
  sp.sf = max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
  sp.color = color;
  return sp;
}

// SDF of a box at the origin given its size (width, height and depth).
Shape dBox(vec3 p, vec3 s, vec3 color) {
    Shape sp;
    sp.color = color;
    sp.sf = length(max(abs(p) - s, 0.0));
    return sp;
}

// SDF of a sphere, given its center and radius.
Shape dSphere(vec3 p, vec4 s, vec3 color) {
    Shape sp ;
    sp.color=color;
    sp.sf=length(p - s.xyz) - s.w;
    return sp;
}

Shape dOcta(vec3 p, float s, vec3 color) {
    Shape sp ;
    sp.color=color;
    p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else{sp.sf= m*0.57735027;return sp;}

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 

    sp.sf= length(vec3(q.x,q.y-s+k,q.z-k));
  return sp;
}

// B + A
Shape unionSDF (Shape spA, Shape spB) {
    return minShape(spA, spB);
}

// B * A
Shape intersectionSDF (Shape spA, Shape spB) {
   if (spB.sf > spA.sf) return spB; 
   return spA;
}

// B - A
Shape differenceSDF (Shape spA, Shape spB) {
    spA.sf*=-1.;
    if (spB.sf > spA.sf) return spB; 
    return spA;
}

// Blend A and B
Shape blendSDF(Shape spA, Shape spB, float k, vec3 color) {
    float h = clamp(0.5 + 0.5 * (spB.sf - spA.sf) / k, 0.0, 1.0);
    Shape sp ;
    sp.color=color;
    sp.sf= mix(spB.sf, spA.sf, h) - k * h * (1.0 - h);
    return sp;
}

// Scene SDF: signed distance function.
Shape getDist(vec3 p) {

     float s = sin(iTime);

    Shape planeDist;
    planeDist.sf = p.y;
    planeDist.color=rgb(vec3(190, 190, 250));
    planeDist.isBumped = true;


    // Position
    vec3 sunP1 = p - vec3(0,1,6);
    float sunDistortion = (sin(mod(2.*iTime,100.) *- p.z) * 0.01) /2.;
    vec3 sunP = sunP1;
    sunP.xz *= Rot(-1.*iTime);


    vec3 mercuryP = p - vec3(0,0,6);
    mercuryP = (mercuryP * rotateY(iTime * 1.) - vec3(1.) * rotateY(iTime * 4.));
    mercuryP.xz *= Rot(1.*iTime);


    vec3 venusP = p - vec3(0,-0.2,6);
    venusP = (venusP * rotateY(iTime * 1.) - vec3(1.2) * rotateY(iTime * 2.5));

    vec3 earthP = p - vec3(0,-0.5,6);
    earthP = (earthP * rotateY(iTime * 1.) - vec3(1.5) * rotateY(iTime * 2.));

    vec3 marsP = p - vec3(0,-1.,6);
    marsP = (marsP * rotateY(iTime * 1.) - vec3(2.) * rotateY(iTime * 1.8));
    marsP.yz *= Rot(1.*iTime);

   float jupyterDistortion = cos(mod(1.*iTime,50.) * p.y)  *  0.01;
   vec3 jupyterP1 = p - vec3(0,-1.8,6) + jupyterDistortion;
    vec3 jupyterP2 = p - vec3(0,-1.5,6) + jupyterDistortion;
    vec3 jupyterP3 = p - vec3(0.5,-1.6,6) ;
    vec3 jupyterP4 = p - vec3(0.6,-1.7,6);
    jupyterP1 = (jupyterP1 * rotateY(iTime * 1.) - vec3(2.5) * rotateY(iTime * 1.3));
    jupyterP2 = (jupyterP2 * rotateY(iTime * 1.) - vec3(2.5) * rotateY(iTime * 1.3));
    jupyterP3 = (jupyterP3 * rotateY(iTime * 1.) - vec3(2.5) * rotateY(iTime * 1.3));
    jupyterP4 = (jupyterP4 * rotateY(iTime * 1.) - vec3(2.5) * rotateY(iTime * 1.3));

    jupyterP1.y += 0.05*s;
    jupyterP2.y -= 0.05*s;
    jupyterP3.x += 0.1*s;
    jupyterP4.x -= 0.1*s;
    jupyterP1.xz *= Rot(1.*iTime);
    jupyterP2.xz *= Rot(1.*iTime);
    jupyterP3.xz *= Rot(1.*iTime);
    jupyterP4.xz *= Rot(1.*iTime);



   float ringDistortion = cos(mod(1.*iTime,100.) * p.z)  *  0.01;

    vec3 saturnP = p - vec3(0,-2.0,6);
    vec3 ringP1 = p - vec3(0,-2.0,6) + ringDistortion;
    vec3 ringP2 = p - vec3(0,-2.0,6) + ringDistortion;
    saturnP = (saturnP * rotateY(iTime * 1.) - vec3(3.2) * rotateY(iTime * 1.24));


    ringP1 = (ringP1 * rotateY(iTime * 1.) - vec3(3.2) * rotateY(iTime * 1.24));
    ringP2 = (ringP2 * rotateY(iTime * 1.) - vec3(3.2) * rotateY(iTime * 1.24));
    saturnP.xz *= Rot(1.*iTime);
    ringP1.xz *= Rot(1.*iTime);
    ringP2.xz *= Rot(1.*iTime);

    vec3 netuneBoxP = p - vec3(0,-2.5,6);
    vec3 netuneSphereP = p - vec3(0,-2.5,6);
    netuneBoxP = (netuneBoxP * rotateY(iTime * -1.) - vec3(3.7) * rotateY(iTime * -0.7));
    netuneSphereP = (netuneSphereP * rotateY(iTime * -1.) - vec3(3.7) * rotateY(iTime * -0.7));
    netuneBoxP.z +=0.05*s;
    netuneBoxP.y +=0.05*s;
    netuneBoxP.xz *= Rot(1.5*iTime);
    netuneBoxP.yz *= Rot(1.5*iTime);

    vec3 uranusP = p - vec3(0,-3,6);
     uranusP = (uranusP * rotateY(iTime * -1.) - vec3(4.2) * rotateY(iTime * -0.9));
    uranusP.xz *= Rot(2.*iTime);
    uranusP.xy *= Rot(2.*iTime);
    uranusP.yz *= Rot(1.5708);


    // sizes
    vec4 sunS = vec4(0, 0, 0 , 1); // Sphere
    vec2 mercuryS = vec2(0.05,0.05); // Prism
    vec3 venusS = vec3(0.11); // Box
    vec4 earthS = vec4(0, 0, 0 , 0.13); // Sphere

    vec4 moonS = vec4(0, 0, 0 , 0.04); // Sphere
    vec2 marsS = vec2(0.06, 0.03); // Torus
    vec4 jupyterS1 = vec4(0, 0, 0 , 0.3); // Blend Sphere
    vec4 jupyterS2 = vec4(0, 0, 0 , 0.2); // Blend Sphere

    vec4 saturnS = vec4(0, 0, 0 , 0.3); // Sphere
    vec2 ringS1 = vec2(0.4, 0.01); // Torus
    vec2 ringS2 = vec2(0.5, 0.01); // Torus

    vec4 netuneSphereS = vec4(0, 0, 0 , 0.26); // Diff Sphere
    vec3 netuneBoxS = vec3(0.22); // Diff Box

    float uranusS =  0.26; // Octo

    // Shapes
    Shape sun = dSphere(sunP, sunS, texture(iChannel2, sunP.xz).xyz * rgb(vec3(255,255,0)) * 1.5);
    // Shape sun = dSphere(sunP, sunS, texture(iChannel1, sunP.xz).xyz);
    sun.isTexturized = true;


    Shape mercury = dPrism(mercuryP, mercuryS, rgb(vec3(99, 87, 74)) );
    Shape venus = dBox(venusP, venusS, rgb(vec3(238,203,139)));
    Shape earth = dSphere(earthP, earthS, rgb(vec3(100, 100, 255)));

    Shape mars = dTorus(marsP, marsS, rgb(vec3(205, 157, 117)));

    Shape jupyter1 = dSphere(jupyterP1, jupyterS1, vec3(149, 120, 90));
    Shape jupyter2 = dSphere(jupyterP2, jupyterS2, vec3(167, 110, 64));
    Shape jupyter3 = dSphere(jupyterP3, jupyterS1, vec3(149, 120, 90));
    Shape jupyter4 = dSphere(jupyterP4, jupyterS2, vec3(167, 110, 64));
    Shape jupyter = blendSDF(
                        blendSDF(jupyter1,jupyter2,0.1,vec3(167, 110, 64)),
                        blendSDF(jupyter1,jupyter2,0.04,vec3(167, 110, 64)),
                        0.3,
                        rgb(vec3(167, 110, 64))
                   );

    Shape saturn = dSphere(saturnP, saturnS, rgb(vec3(234,214,184)));

    Shape ring1 = dTorus(ringP1, ringS1, rgb(vec3(227,224,192)));
    Shape ring2 = dTorus(ringP2, ringS2, rgb(vec3(206,206,206)));

    Shape netune1 = dBox(netuneBoxP, netuneBoxS, rgb(vec3(238,203,139)));
    Shape netune2 = dSphere(netuneSphereP, netuneSphereS, rgb(vec3(230, 230, 230)));
    Shape netune = differenceSDF(netune2,netune1);

    Shape uranus =  dOcta(uranusP, uranusS, rgb(vec3(172, 211, 216)));


    Shape d = minShape(planeDist, sun);
    d = minShape(d, mercury);
    d = minShape(d, venus);
    d = minShape(d, earth);
    d = minShape(d, mars);
    d = minShape(d, jupyter);
    d = minShape(d, saturn);
    d = minShape(d, ring1);
    d = minShape(d, ring2);
    d = minShape(d, netune);
    d = minShape(d, uranus);

    return d;
}

// The normal is the gradient of the SDF scalar field at point p.
// By using finite difference: 
// https://en.wikipedia.org/wiki/Finite_difference#Relation_with_derivatives 
vec3 getNormal(vec3 p) {
    // arbitrary â€” should be smaller than any surface detail in your distance function, 
    // but not so small as to get lost in float precision
    vec2 e = vec2(0.001, 0);

    vec3 n = 
        vec3(
        getDist(p + e.xyy).sf,
        getDist(p + e.yxy).sf,
        getDist(p + e.yyx).sf) - 
        vec3(
        getDist(p - e.xyy).sf,
        getDist(p - e.yxy).sf,
        getDist(p - e.yyx).sf);

    return normalize(n);
}

vec3 getNormalBumped(vec3 p) {

    vec2 texCoord = vec2(p.x, p.z); // calculate texture coordinates
    vec3 bump = texture(iChannel1, texCoord).rgb; // sample bump map
    bump = bump * 2.0 - 1.0; // convert from [0, 1] to [-1, 1] range
    vec3 n = getNormal(p); // get normal from distance field
    n += bump; // perturb normal using bump map
    return normalize(n);


}


// Walk through empty spheres until the ray hits an object
// or is beyond MAX_DIST, meaning no hit. 
Shape rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    Shape sp;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        Shape distShape= getDist(p);
        if (distShape.isTexturized) {
            sp.color = distShape.color;
            sp.isTexturized = true;
        } else {
            sp.isTexturized = false;
          sp.color= g2l3(distShape.color);
        }

        if (distShape.isBumped) {
           sp.isBumped = true;
        } else {

           sp.isBumped = false;
        }

        float dS = distShape.sf;
        dO += dS;
        if (dO > MAX_DIST || dS < SURF_DIST) break;
    }
    sp.sf= dO;
    return sp;
}


// Phong illumination model, given a point and camera position.
float getLight(vec3 p, vec3 ro, Shape a) {
    vec3 lightPos = vec3(-2, 5, 0);

    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 1.5;

    vec3 l = normalize(lightPos - p);

    vec3 n;

    if (a.isBumped) {
       n = getNormalBumped(p);
    } else {
       n = getNormal(p);
    }
    vec3 r = reflect(-l,n);

    // ambient component
    float amb = 0.1;
    // ambient reflection coefficient
    float ka = 1.0;

    // diffuse component - apply Lambert's cosine law
    float dif = clamp(dot(n, l), 0.0, 1.0);
    // diffuse reflection coefficient
    float kd = 0.8;

    // specular component
    float shininess = 100.;
    vec3 v = normalize(ro-p);
    float spec = clamp(dot(r, v), 0.0, 1.0);
    spec = pow(spec,shininess);
    // specular reflection coefficient
    float ks = 1.0;

    // slightly move point p in the direction of the normal,
    // so the distance is not zero. Otherwise, the first hit 
    // will be at the plane.
    Shape d = rayMarch(p + 2. * n * SURF_DIST , l);

    // cast a ray to the light
    if (d.sf < length(lightPos - p)) {
        // point in the shadow
        dif *= 0.1;
        spec *= 0.5;
    }

    // ambient + diffuse + specular components
    return ka * amb + kd * dif + ks * spec;
}

// Fragment shader.
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera position
    vec3 ro = vec3(0, 2.3, -2.7);

    // projection plane z = 1
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    // Rotate camera down about the x-axis
    rd.yz *= Rot(PI * 10. / 180.); 

    Shape d = rayMarch(ro, rd);

    // point hit by the ray
    vec3 p = ro + rd * d.sf;

    // illumination at point p
    float light_intensity = getLight(p, ro, d);

    // dark grey
    // vec3 background_color = vec3(.2, .2, .2);
    vec3 background_color = texture(iChannel0, uv*vec2(1.,2.)).rgb ;

    // Antique White color #FAEBD7
    vec3 surface_color = vec3(0,255,255) / 255.;

    // pixel color
    vec3 color;
    if (d.sf < MAX_DIST) {


       color = d.color * vec3(light_intensity);


    } else {
        color = background_color;
    }

    // output to screen
    fragColor = vec4(color,1.0);
}
