void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 FC = fragCoord.xy;
    float r = iResolution.y * 0.5;
    float t = iTime;

    vec3 p;
    float e, g, S, u;

    g = 0.0;
    for (float i = 0.0; i < 100.0; i++)
    {
        p = vec3((FC - 0.5 * vec2(iResolution.xy)) / r * g, g - 4.0);
        p.xz *= mat2(cos(t), sin(t), -sin(t), cos(t));
        p--;
        S = 4.0;
        for (int j = 0; j < 9; j++)
        {
            S /= u = dot(p, p);
            p /= u;
            p = abs(p + vec3(1.0)) - vec3(1.0);
        }
        g += e = abs(p.y) / S;
    }
    fragColor = vec4(normalize(++p).y, normalize(++p).y, normalize(++p).y, 1.0);
}
