#define lines -iMouse.y/25.
#define zoom 1.
#define fps 60.
#define speed 25. // higher = slower
#define glow 1.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fade = iMouse.x/25.;

    if(fade > 5.0) {  // so it on default doesnt look dogshit (remove if u want idgaf)
        fade = fade;
    } else if (fade < 5.0) {
        fade = 5.;
    }

    vec2 uv = fragCoord/iResolution.xy; // get uv coords [0,1]

    float d = length(uv.y*floor(lines - 10.)); // make lines by getting the length of uv (specifically the y axis)

    d = sin(d*zoom + floor(iTime*fps)/speed)/3.; // loop the lines with sin ( u can remove the "floor( ### )" [i kinda like it more] )

    d = abs(d * fade); // do fade line shit

    vec3 col = vec3(glow+-d); // add the lines to da uv

    col += 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)); // add rgb

    fragColor = vec4(col,1.0);
}
