float h( ivec2 x ) {
    ivec2 q = 1103515245 *  (x>>1 ^ x.yx)   ;
    return float((1103515245 * (q.x^q.y>>3))) * (1./float(0xffffffff)); }
void mainImage(out vec4 o,in vec2 p){
    vec2 u = p/iResolution.y+iTime*.2,
         F=fract(u*9.-.5)*2.-1.;
    float d=1.;
    for(float i=0.;i<4.;i++){
        vec2 k = round(u*9.)+.1*(abs(mod(vec2(i,i-1.),4.)-2.)-1.);
        d=min(d,max(h(ivec2(k.x+k.y,k.y-k.x))<.5?0.:1.,
          length(F-vec2(max(F.x,0.),0)))), F=vec2(F.y,-F.x); }
    o=vec4(vec3(smoothstep(0.,.05,abs(d-.5*.7)-.1)),1); }

