void mainImage(out vec4 fragColor, in vec2 fragCoord) {
 vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
 vec3 col = vec3(0.6,0.2,0.2);

 uv *= 3.0;

 float g_cd = length(uv);

 uv = fract(uv) - 0.5;

 uv.x = pow(abs(uv.x), 0.66);
 uv.y += -uv.x;
 uv.y *= 1.2;
 uv.x *= mix(0.3, 1.0, uv.y+1.0);

 uv.y += 0.15;

 float cd = length(uv);

 float t = 3.2*iTime + g_cd;

 cd -= 0.0625+0.125*sin(1.1*t);

 col += step(cd, 0.33);

 col *= vec3(1.0,0.5,0.5);

 col *= mix(vec3(1), vec3(0.8,0.03,0.03), smoothstep(0.33, 0.98, 0.5+0.5*sin(1.01*t)));

 fragColor = vec4(col,1.0);
}
