float hash(float n) { return fract(sin(iTime*0.01 + n * 43758.5453)); }

// Define a function to generate noise.
vec2 noise(vec2 p)
{
    float px = p.x + iTime*0.001;
    return vec2(hash(px/p.y),hash(p.y*px));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get the fragment's UV coordinates.
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Generate noise.
    vec2 noiseValue = noise(uv);

    // Calculate the flame color.
    vec3 color = vec3(noiseValue.x, noiseValue.y, 1.0);

    // Set the fragment color.
    fragColor = vec4(color, 1.0);
}
